con:config.script:
import com.eviware.soapui.model.testsuite.Assertable

def lastResult = testRunner.testCase.testSteps["LoadCPSWithAlerts"]
def lastResultName = lastResult.getTestStep().getLabel().toString()
def lastResultStatus = lastResult.getTestStep().getAssertionStatus().toString()

log.info 'Test Step [' + lastResultName + '] status: ' + lastResultStatus


if ( lastResultStatus.contains('FAILED')) {
	testRunner.gotoStepByName("PropertyTransfer 3")
	log.info 'Goto step Output Data'
}
else {
		testRunner.gotoStepByName("LoadCPSWithAlerts")
		log.info 'Continue to next step'
		log.info lastResultName
		log.info lastResultStatus
}

